            },
          }}
        >
          {
            data.data.data.map((movie, index) => {
              return (
                <Paper elevation={3}>
                  <IconButton aria-label="settings">
                    <MoreVertIcon />
                  </IconButton>
                  <img src="https://i.blogs.es/89c7de/uncharted-cartel/1366_2000.jpeg" width="100px" />
                  <DeleteMovie id={movie._id} />
                  <UpdateMovie id={movie._id} />
                </Paper>
              )
