      //     footer: 'Copyright 2018',
      //   }).then(() => {
      //     return MySwal.fire(<p>Shorthand works too2</p>)
      //   })
        // window.confirm(
        //     `Do tou want to delete the movie ${props.id} permanently?`,
        // )
    // ) {
        //alert(props.id)
        //mutate(props.id)
        //stopPropagation()
        //api.deleteMovieById(props.id)
